mapper.update:
update category set category_parent_id=null,category_categoryid=null where category_id = #{categoryId}; update category
category_title=#{categoryTitle}, category_sort=#{categorySort}, category_datetime=#{categoryDateTime}, category_managerid=#{categoryManagerId}, category_categoryid=#{categoryCategoryId}, category_smallimg=#{categorySmallImg}, category_description=#{categoryDescription}, category_parent_id=#{categoryParentId}, category_dict_id=#{categoryDictId}, update_date=#{updateDate}, update_by=#{updateBy}
where category_id = #{categoryId}

mapper.delete:
update category set del = 1
where category_id in

	#{id}

mapper.select:
select * from category

	and category_title=#{categoryTitle}
	and category_sort=#{categorySort}
	and category_datetime=#{categoryDateTime}
	and category_managerid=#{categoryManagerId}
	and category_categoryid=#{categoryCategoryId}
	and category_smallimg=#{categorySmallImg}
	and category_description=#{categoryDescription}
	and category_parent_id=#{categoryParentId}
	and category_dict_id=#{categoryDictId}
	and update_date=#{updateDate}
	and update_by=#{updateBy}
	and del=0

limit 0,1

select

from category c where category_modelId=#{category.categoryModelId}

	and category_managerid=#{category.categoryManagerId}


	and category_categoryid=#{category.categoryCategoryId}


	and category_appid=#{category.categoryAppId}

and del=0

	order by ${orderBy}

asc
desc

select

from category where category_modelId=#{categoryModelId}
 and CATEGORY_TITLE like CONCAT('%',#{categoryTitle},'%') 
 and CATEGORY_DATETIME=#{categoryDateTime} 

	and category_managerid=#{categoryManagerId}


	and category_categoryid=#{categoryCategoryId}


	and category_appid=#{categoryAppId}


	and CATEGORY_DICT_ID=#{categoryDictId}

and del=0
order by category_id desc

select

from category where category_modelId=#{categoryModelId}

	and CATEGORY_DICT_ID=#{categoryDictId}

and del=0
order by category_id desc

select * from category 


		and category_modelId=#{categoryModelId}


		and category_appid=#{categoryAppId}


		and CATEGORY_DICT_ID=#{categoryDictId}

	and
	(

		CATEGORY_PARENT_ID like
		'%${categoryParentId}%'


		find_in_set('${categoryId}',CATEGORY_PARENT_ID)


		or category_id=#{categoryId}

	)
	and del=0

select

from category


		and category_appId=#{appId}


		and category_modelId=#{modelId}


		and category_description =#{categoryDescription}

	and del=0

select

from category c where category_modelId=#{category.categoryModelId}


	and category_managerid=#{category.categoryManagerId}



	and category_categoryid=#{category.categoryId}


and del=0

order by category_id asc;

select count(*) from category c where
category_modelId=#{category.categoryModelId} and
category_managerid=#{category.categoryManagerId}

	and category_categoryid=#{category.categoryCategoryId}

and del=0

select * from category


		and category_appId=#{appId}


		and category_modelId=#{modelId}

	and del=0
